mapper.select:
select

from sys_big_text
where id = #{id}


	and tenant_id = #{tenantId}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

limit 1

select

from sys_big_text
where text_title LIKE CONCAT('%',#{textTitle},'%')


	and tenant_id = #{tenantId}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

select

from sys_big_text
where text_code = #{textCode}


	and tenant_id = #{tenantId}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

order by create_date desc

select

from sys_big_text
where text_code in

	#{item}



	and tenant_id = #{tenantId}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

order by create_date desc

select

from sys_big_text
where id in

	#{item}



	and tenant_id = #{tenantId}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

order by create_date desc

select

from sys_big_text



		and id = #{id}


		and text_title = #{textTitle}


		and text_code = #{textCode}


		and text_content = #{textContent}


		and state_enum = #{stateEnum}


		and tenant_id = #{tenantId}


		and create_user_id = #{createUserId}


		and delete_enum = #{deleteEnum}


		and create_date >= #{createDateStartDate}


		and create_date <= #{createDateEndDate}


		and update_date >= #{updateDateStartDate}


		and update_date <= #{updateDateEndDate}




		order by

			${item}



		order by create_date desc

select

from sys_big_text
where state_enum = #{stateEnum}


	and tenant_id = #{tenantId}

order by create_date desc

select

from sys_big_text
where delete_enum = #{deleteEnum}


	and tenant_id = #{tenantId}

order by create_date desc

select

from sys_big_text
where delete_enum = #{deleteEnum} and state_enum = #{stateEnum}


	and tenant_id = #{tenantId}

order by create_date desc

mapper.update:
update sys_big_text
set
state_enum = #{stateEnum},
update_date = #{updateDate},
update_user_id = #{updateUserId}

where id in

	#{item}


	and tenant_id = #{tenantId}

update sys_big_text
set
delete_enum = #{deleteEnum},
delete_date = #{deleteDate},
delete_user_id = #{deleteUserId}

where id in

	#{item}


	and tenant_id = #{tenantId}
