mapper.select:
select

from sys_dict_item
where id = #{id}


	and tenant_id = #{tenantId}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

limit 1

select

from sys_dict_item
where dict_id = #{dictId}


	and tenant_id = #{tenantId}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

limit 1

select

from sys_dict_item
where dict_id = #{dictId}


	and tenant_id = #{tenantId}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

order by ranking asc

