mapper.select:
select

from sys_employee
where id = #{id}


	and tenant_id = #{tenantId}


	and delete_enum = #{deleteEnum}

limit 1

select

from sys_employee
where user_id = #{userId}


	and tenant_id = #{tenantId}


	and delete_enum = #{deleteEnum}

limit 1

select

from sys_employee
where user_id = #{userId}


	and tenant_id = #{tenantId}


	and delete_enum = #{deleteEnum}

order by create_date desc

