mapper.select:
select

from sys_login_log
where id = #{id}


	and tenant_id = #{tenantId}

limit 1

select

from sys_login_log
where user_id = #{userId}


	and tenant_id = #{tenantId}

limit 1

select

from sys_login_log
where user_id = #{userId}


	and tenant_id = #{tenantId}

order by create_date desc

select

from sys_login_log
where user_id in

	#{item}



	and tenant_id = #{tenantId}

order by create_date desc

select

from sys_login_log
where token = #{token}


	and tenant_id = #{tenantId}

order by create_date desc

select

from sys_login_log
where username = #{username}


	and tenant_id = #{tenantId}

order by create_date desc

select

from sys_login_log
where username in

	#{item}



	and tenant_id = #{tenantId}

order by create_date desc

select

from sys_login_log
where id in

	#{item}



	and tenant_id = #{tenantId}

order by create_date desc

select

from sys_login_log



		and id = #{id}


		and user_id = #{userId}


		and username = #{username}


		and client_id = #{clientId}


		and token = #{token}


		and message = #{message}


		and login_date = #{loginDate}


		and logout_date = #{logoutDate}


		and request_url = #{requestUrl}


		and bool_login_success_enum = #{boolLoginSuccessEnum}


		and bool_now_online_enum = #{boolNowOnlineEnum}


		and offline_type_enum = #{offlineTypeEnum}


		and exception_msg = #{exceptionMsg}


		and tenant_id = #{tenantId}


		and ip_address = #{ipAddress}


		and ip_region = #{ipRegion}


		and ip_region_country = #{ipRegionCountry}


		and ip_region_province = #{ipRegionProvince}


		and ip_region_city = #{ipRegionCity}


		and ip_region_isp = #{ipRegionIsp}


		and user_agent = #{userAgent}


		and device_name = #{deviceName}


		and os_name = #{osName}


		and browser_name = #{browserName}


		and browser_locale = #{browserLocale}


		and bool_new_user_enum = #{boolNewUserEnum}


		and login_origin_enum = #{loginOriginEnum}


		and create_user_id = #{createUserId}


		and login_date >= #{loginDateStartDate}


		and login_date <= #{loginDateEndDate}


		and logout_date >= #{logoutDateStartDate}


		and logout_date <= #{logoutDateEndDate}


		and create_date >= #{createDateStartDate}


		and create_date <= #{createDateEndDate}




		order by

			${item}



		order by create_date desc

