mapper.select:
select

from sys_permission
where id = #{id}


	and tenant_id = #{tenantId}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

limit 1

select

from sys_permission
where permission_code = #{permissionCode}


	and tenant_id = #{tenantId}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

order by ranking asc

select

from sys_permission
where permission_code in

	#{item}



	and tenant_id = #{tenantId}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

order by ranking asc

select

from sys_permission
where parent_id = #{parentId}


	and tenant_id = #{tenantId}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

order by ranking asc

select

from sys_permission
where parent_id in

	#{item}



	and tenant_id = #{tenantId}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

order by ranking asc

select

from sys_permission
where id in

	#{item}



	and tenant_id = #{tenantId}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

order by ranking asc

select

from sys_permission



		and id = #{id}


		and permission_name = #{permissionName}


		and permission_code = #{permissionCode}


		and permission_url = #{permissionUrl}


		and belong_type_enum = #{belongTypeEnum}


		and permission_type_enum = #{permissionTypeEnum}


		and parent_id = #{parentId}


		and parent_ids = #{parentIds}


		and icon_class = #{iconClass}


		and visible_enum = #{visibleEnum}


		and bool_ext_link_enum = #{boolExtLinkEnum}


		and bool_new_tab_enum = #{boolNewTabEnum}


		and ranking = #{ranking}


		and description = #{description}


		and state_enum = #{stateEnum}


		and tenant_id = #{tenantId}


		and create_user_id = #{createUserId}


		and delete_enum = #{deleteEnum}


		and create_date >= #{createDateStartDate}


		and create_date <= #{createDateEndDate}


		and update_date >= #{updateDateStartDate}


		and update_date <= #{updateDateEndDate}




		order by

			${item}



		order by ranking asc

select

from sys_permission
where state_enum = #{stateEnum}


	and tenant_id = #{tenantId}

order by ranking asc

select

from sys_permission
where delete_enum = #{deleteEnum}


	and tenant_id = #{tenantId}

order by ranking asc

select

from sys_permission
where delete_enum = #{deleteEnum} and state_enum = #{stateEnum}


	and tenant_id = #{tenantId}

order by ranking asc

mapper.update:
